import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RecipientManagement.css';

const RecipientManagement = () => {
    const [recipients, setRecipients] = useState([]);
    const [newRecipient, setNewRecipient] = useState({
        userID: '',
        donationID: '',
        quantityReceived: ''
    });
    const [users, setUsers] = useState([]);
    const [donations, setDonations] = useState([]);

    useEffect(() => {
        // Fetch recipients from API
        axios.get('http://localhost:8080/recipients')
            .then(response => {
                setRecipients(response.data);
            })
            .catch(error => console.error('Error fetching recipients:', error));

        // Fetch users and donations from API for selection
        axios.get('http://localhost:8080/users')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => console.error('Error fetching users:', error));

        axios.get('http://localhost:8080/donations')
            .then(response => {
                setDonations(response.data);
            })
            .catch(error => console.error('Error fetching donations:', error));
    }, []);

    const handleAddRecipient = () => {
        axios.post('http://localhost:8080/recipients', newRecipient)
            .then(response => {
                setRecipients([...recipients, response.data]);
                setNewRecipient({
                    userID: '',
                    donationID: '',
                    quantityReceived: ''
                });
            })
            .catch(error => console.error('Error adding recipient:', error));
    };

    return (
        <div className="recipient-management">
            <h1>Recipient Management</h1>
            <div className="recipient-form">
                <select
                    value={newRecipient.userID}
                    onChange={e => setNewRecipient({ ...newRecipient, userID: e.target.value })}
                >
                    <option value="">Select User</option>
                    {users.map(user => (
                        <option key={user.userID} value={user.userID}>
                            {user.userName}
                        </option>
                    ))}
                </select>
                <select
                    value={newRecipient.donationID}
                    onChange={e => setNewRecipient({ ...newRecipient, donationID: e.target.value })}
                >
                    <option value="">Select Donation</option>
                    {donations.map(donation => (
                        <option key={donation.donationID} value={donation.donationID}>
                            {donation.donationDescription}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    value={newRecipient.quantityReceived}
                    onChange={e => setNewRecipient({ ...newRecipient, quantityReceived: e.target.value })}
                    placeholder="Quantity Received"
                />
                <button onClick={handleAddRecipient}>Add Recipient</button>
            </div>
            <ul>
                {recipients.map(recipient => (
                    <li key={recipient.id}>
                        UserID: {recipient.userID}, DonationID: {recipient.donationID}, Quantity Received: {recipient.quantityReceived}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RecipientManagement;
