import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, TextField, Select, MenuItem, InputLabel, FormControl } from '@mui/material';
import './UserManagement.css';

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState('');
    const [editUser, setEditUser] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8080/users')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    const handleAddUser = () => {
        axios.post('http://localhost:8080/users', { name: newUser })
            .then(response => {
                setUsers([...users, response.data]);
                setNewUser('');
            })
            .catch(error => console.error('Error adding user:', error));
    };

    const handleUpdateUser = () => {
        if (editUser) {
            axios.put(`http://localhost:8080/users/${editUser.id}`, { name: editUser.name })
                .then(response => {
                    const updatedUsers = users.map(user =>
                        user.id === editUser.id ? response.data : user
                    );
                    setUsers(updatedUsers);
                    setEditUser(null);
                })
                .catch(error => console.error('Error updating user:', error));
        }
    };

    const handleDeleteUser = (id) => {
        axios.delete(`http://localhost:8080/users/${id}`)
            .then(() => {
                const updatedUsers = users.filter(user => user.id !== id);
                setUsers(updatedUsers);
            })
            .catch(error => console.error('Error deleting user:', error));
    };

    const handleEditChange = (e) => {
        setEditUser({ ...editUser, name: e.target.value });
    };

    return (
        <div className="user-management">
            <h1>User Management</h1>
            <div className="user-form">
                <TextField
                    label="New User Name"
                    value={newUser}
                    onChange={e => setNewUser(e.target.value)}
                />
                <Button onClick={handleAddUser} variant="contained">Add User</Button>
            </div>
            {editUser && (
                <div className="user-edit-form">
                    <h2>Edit User</h2>
                    <TextField
                        label="User Name"
                        value={editUser.name}
                        onChange={handleEditChange}
                    />
                    <Button onClick={handleUpdateUser} variant="contained">Update User</Button>
                    <Button onClick={() => setEditUser(null)} variant="outlined">Cancel</Button>
                </div>
            )}
            <ul>
                {users.map(user => (
                    <li key={user.id}>
                        {user.name}
                        <Button onClick={() => setEditUser({ id: user.id, name: user.name })} variant="outlined">Edit</Button>
                        <Button onClick={() => handleDeleteUser(user.id)} variant="outlined" color="error">Delete</Button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManagement;
