// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
    const [data, setData] = useState({
        totalDonations: 0,
        totalCategories: 0,
        totalRecipients: 0,
    });

    // Fetch data from the API
    useEffect(() => {
        const fetchData = async () => {
            try {
                const donationsResponse = await axios.get('/api/donations/count');
                const categoriesResponse = await axios.get('/api/categories/count');
                const recipientsResponse = await axios.get('/api/recipients/count');

                setData({
                    totalDonations: donationsResponse.data.count,
                    totalCategories: categoriesResponse.data.count,
                    totalRecipients: recipientsResponse.data.count,
                });
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <h1>Dashboard</h1>
            </div>
            <div className="dashboard-cards">
                <div className="card">
                    <h3>Total Donations</h3>
                    <p>{data.totalDonations}</p>
                </div>
                <div className="card">
                    <h3>Total Categories</h3>
                    <p>{data.totalCategories}</p>
                </div>
                <div className="card">
                    <h3>Total Recipients</h3>
                    <p>{data.totalRecipients}</p>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
